  created() {
    this.apiStatus()
    setInterval(() => {
      this.apiStatus()
    }, this.interval)
  },
  methods: {
    date(begin) {
      begin = parseInt(begin)
      const current = new Date().getFullYear()
      const year = `${begin} - ${current}`
      if (begin === current) {
        return begin
      }
      return year
    },
    async apiStatus() {
      let res = null
      try {
        res = await this.$http.head(`${process.env.API_URL}/api`)
        if (res.status === 200) {
          this.apiStatusBoolean = true
        } else {
          this.apiStatusBoolean = false
        }
      } catch (error) {
        this.apiStatusBoolean = false
      }
    },
  },
}
</script>

